// Define an array of variable names used in the previous functions
var variableNames = [
    "LastBalance",
    "BalanceInterval",
    "BalanceTime",
    "MaxSoc",
    "RestartSOC",
    "ChargeEnabled",
    "ChargeToFull",
    "VesselInput",
    "MultiState",
    "BattSoc",
    "AcFixed",
    "CurrentLimitOverride",
    "ShoreMaxCurrent",
    "LastBalanceDate",
    "BmsBalancing",
    "SolarMgmt",
    "ChargeEnabledSol",
];

// Create an object to store the values
var globalValues = {};

// Retrieve the values from global context
variableNames.forEach(function (variableName) {
    var value = global.get(variableName);
    globalValues[variableName] = value;
});

// Send the object in the output message
msg.payload = globalValues;

// Pass the message to the next node
return msg;

const currentTimestamp = Date.now();
const LastBalance = flow.get("LastBalance");
const BalanceInterval = flow.get("BalanceInterval");
let ChargeToFull = global.get("ChargeToFull");

if (ChargeToFull === 1) {
    node.status({fill: "yellow", shape: "dot", text: "Charge to Full"});
    return;
} else if (ChargeToFull === 0) {
    const millisSinceLastBalance = currentTimestamp - LastBalance;
    const millisBalanceInterval = BalanceInterval * 24 * 60 * 60 * 1000;
    if (millisSinceLastBalance >= millisBalanceInterval) {
        node.status({fill: "yellow", shape: "dot", text: "Charge to Full"});
        return {
            payload: 1,
        };
    }
}

node.status({fill: "green", shape: "dot", text: "Normal Operation"});

const timestamp = msg.payload;
const date = new Date(timestamp);
const formattedDate = date.toISOString().slice(0, 10);

return {
    payload: formattedDate,
}

const cvl = msg.payload;
const NORMAL_CVL = 28.35;

if (cvl > NORMAL_CVL) {
    node.status({fill: "green", shape: "dot", text: "Requesting Absorption"});
    return {payload: 1};
}

node.status({fill: "red", shape: "dot", text: "Not Requesting Absorption"});
return {payload: 0};

const BALANCING_THRESHOLD = 28.35;

const voltage = msg.payload;

const balancing = voltage >= BALANCING_THRESHOLD;

if (balancing) {
    node.status({fill: "green", shape: "dot", text: "Balancing"});
} else {
    node.status({fill: "green", shape: "ring", text: "Idle"});
}

return {
    payload: + balancing,  // bool -> int
};

const LastBalance = flow.get("LastBalance");

return {
    payload: {
        LastBalance,
    },
}

const balancing = msg.payload;
const now = msg.timestamp;

if (balancing == 0) {
    node.status({fill: "grey", shape: "ring", text: "Idle"});
    return;
}

const BalanceStartTime = flow.get("BalanceStartTime");
const BalanceTime = flow.get("BalanceTime");
const BalanceTimeMillis = BalanceTime * 60 * 1000;

if (now > BalanceStartTime + BalanceTimeMillis) {
    node.status({fill: "green", shape: "dot", text: "Done!"});
    return {
        payload: 0,
    }
} else {
    node.status({fill: "yellow", shape: "dot", text: "Balancing..."});
}

const secondsAgo = msg.payload;

const now = new Date();
const last_full = new Date(now.getTime() + secondsAgo * 1000);
const formatted_last_full = last_full.toISOString().slice(0, 10);

return {
    payload: formatted_last_full,
};

const BMSRequestAbsorption = flow.get("BMSRequestAbsorption");

// Let the batteries continue to balance as long as the BMS wants to
if (msg.payload >= 99.5 && BMSRequestAbsorption === 0) {
    return msg;
}

const ChargeToFull = global.get("ChargeToFull");
const MultiState = global.get("MultiState");
const BattSoc = global.get("BattSoc");
const MaxSoc = global.get("MaxSoc");
const MultiRestartSoc = global.get("MultiRestartSoc");
let MultiChargeEnabled = global.get("MultiChargeEnabled");

const MultiModes = Object.freeze({
    CHARGER_ONLY: 1,
    INVERTER_ONLY: 2,
    ON: 3,
    OFF: 4,
});

if (MultiChargeEnabled === 0) {
    if (ChargeToFull === 1) {
        MultiChargeEnabled = 1;
    }
    if (BattSoc <= MultiRestartSoc) {
        MultiChargeEnabled = 1;
    }
} else if (MultiChargeEnabled === 1) {
    if (BattSoc >= MaxSoc) {
        if (ChargeToFull === 1) {
            node.status({fill: "yellow", shape: "dot", text: "Balancing"});
            return;
        }
        MultiChargeEnabled = 0;
    }
} else if (MultiChargeEnabled === undefined) {
    node.warn("Startup: Guessing MultiChargeEnabled from MultiState: " + MultiState);
    if (MultiState === MultiModes.INVERTER_ONLY
        || MultiState === MultiModes.OFF) {
        MultiChargeEnabled = 0;
    } else if (MultiState === MultiModes.CHARGER_ONLY
               || MultiState == MultiModes.ON) {
        MultiChargeEnabled = 1;
    }
}

if (MultiChargeEnabled === 1) {
    node.status({fill: "green", shape: "dot", text: "Enable"});
} else {
    node.status({fill: "red", shape: "dot", text: "Disable"});
}

return {
    payload: MultiChargeEnabled,
};

const MultiSOCmgmt = global.get("MultiSOCmgmt");
let MultiChargeEnabled = global.get("MultiChargeEnabled");
const MultiState = global.get("MultiState");

const MultiModes = Object.freeze({
    CHARGER_ONLY: 1,
    INVERTER_ONLY: 2,
    ON: 3,
    OFF: 4,
});

if (MultiSOCmgmt !== 1) {
    node.status({fill: "red", shape: "ring", text: "Management Disabled"});
    return;
}

if (MultiChargeEnabled === 0) {
    if (MultiState === MultiModes.CHARGER_ONLY) {
        node.status({fill: "red", shape: "dot", text: "Off"});
        return {
            payload: MultiModes.OFF,
        };
    } else if (MultiState === MultiModes.ON) {
        node.status({fill: "red", shape: "dot", text: "Charging off"});
        return {
            payload: MultiModes.INVERTER_ONLY,
        };
    }
} else if (MultiChargeEnabled === 1) {
    if (MultiState === MultiModes.OFF) {
        node.status({fill: "green", shape: "dot", text: "On"});
        return {
            payload: MultiModes.CHARGER_ONLY,
        };
    } else if (MultiState === MultiModes.INVERTER_ONLY) {
        node.status({fill: "green", shape: "dot", text: "Charging on"});
        return {
            payload: MultiModes.ON,
        };
    }
}

var MultiSOCmgmt = global.get("MultiSOCmgmt");
var MultiChargeEnabled = global.get("MultiChargeEnabled");
var MaxSoc = global.get("MaxSoc");
var BattSoc = global.get("BattSoc");
var MultiState = global.get("MultiState");
var ChargeToFull = global.get("ChargeToFull");
var MultiRestartSoc = global.get("MultiRestartSoc");
var CycleMgmt = global.get("CycleMgmt");


return {
    payload: {
        ChargeToFull,
        MultiSOCmgmt,
        MultiChargeEnabled,
        MaxSoc,
        BattSoc,
        MultiState,
        CycleMgmt,
        MultiRestartSoc,
    },
};

const CycleMgmt = global.get("CycleMgmt");
const StorMaxSoc = global.get("StorMaxSoc");
const SailMaxSoc = global.get("SailMaxSoc");

const CycleMgmtModes = Object.freeze({
    OFF: 1,
    STORAGE: 2,
    SAILING: 3,
});

let MaxSoc = null;

if (CycleMgmt === CycleMgmtModes.OFF) {
    MaxSoc = null;
} else if (CycleMgmt === CycleMgmtModes.STORAGE) {
    MaxSoc = StorMaxSoc;
} else if (CycleMgmt === CycleMgmtModes.SAILING) {
    MaxSoc =  SailMaxSoc;
}

node.status({text: MaxSoc});
return {
    payload: MaxSoc,
}

const CycleMgmt = global.get("CycleMgmt");
const StorRestartSoc = global.get("StorRestartSoc");
const SailRestartSoc = global.get("SailRestartSoc");

const CycleMgmtModes = Object.freeze({
    OFF: 1,
    STORAGE: 2,
    SAILING: 3,
});

let MultiRestartSoc = null;

if (CycleMgmt === CycleMgmtModes.OFF) {
    MultiRestartSoc = null;
} else if (CycleMgmt === CycleMgmtModes.STORAGE) {
    MultiRestartSoc = StorRestartSoc;
} else if (CycleMgmt === CycleMgmtModes.SAILING) {
    MultiRestartSoc =  SailRestartSoc;
}

node.status({text: MultiRestartSoc});
return {
    payload: MultiRestartSoc,
}

const SolarMgmt = flow.get("SolarMgmt");
const ChargeEnabledSol = global.get("ChargeEnabledSol");

const SolarMgmtStates = Object.freeze({
    ON: 1,
    OFF: 4,
    AUTO: 20,
});

let SolarState;

if (SolarMgmt === SolarMgmtStates.OFF || SolarMgmt === SolarMgmtStates.ON) {
    SolarState = SolarMgmt;
} else if (SolarMgmt === SolarMgmtStates.AUTO) {
    if (ChargeEnabledSol === 0) {
        SolarState = SolarMgmtStates.OFF;
    } else if (ChargeEnabledSol === 1) {
        SolarState = SolarMgmtStates.ON;
    }
}

if (SolarState === SolarMgmtStates.ON) {
    node.status({fill: "green", shape: "dot", text: "On"});
} else if (SolarState === SolarMgmtStates.OFF) {
    node.status({fill: "red", shape: "dot", text: "Off"});
}

return {
    payload: SolarState,
};

const BattSoc = global.get("BattSoc");
const ChargeToFull = global.get("ChargeToFull");
const SailMaxSoc = global.get("SailMaxSoc");
const SolarMgmt = flow.get("SolarMgmt");

const SolarMgmtStates = Object.freeze({
    ON: 1,
    OFF: 4,
    AUTO: 20,
});

if (SolarMgmt === SolarMgmtStates.OFF) {
    node.status({fill: "red", shape: "ring", text: "Manual Disable"});
    return {
        payload: 0,
    }
}
if (BattSoc > SailMaxSoc) {
    if (ChargeToFull === 1) {
        node.status({fill: "yellow", shape: "dot", text: "Charging to Full"});
        return {
            payload: 1,
        };
    }
    node.status({fill: "red", shape: "dot", text: "Disable"});
    return {
        payload: 0,
    };
}

node.status({fill: "green", shape: "dot", text: "Enable"});
return {
    payload: 1,
};
